
public class Typy {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(2+1);
		System.out.println(""+2+1);
		System.out.println('a'+'b');
		System.out.println("a"+"b");
		System.out.println(true);
		System.out.println("cudzysłów \"");
		System.out.println(2.0+3.0);
	}

}
